INSERT INTO `t_ckplayer_video` VALUES ('1487', 'https://v.xw0371.com/20171122/h7vf4RKc/index.m3u8', 'video/m3u8', 'BD1280高清中字', '0', '永生的海拉', null, '故事根据Rebecca Skloot的纪实文学改编电影观看标题人物Henrietta Lacks是上世纪五十年代一位身患癌症的黑人女性，1951年医学研究者非法从她身上获取了癌细胞，结果却引出了令人震惊的医学突破——不仅改变了许多人的命运，甚至改变了整个医学界。据称当时Henrietta Lacks的细胞被用来创造第一株「不死的」人体干细胞——在接受植入后的一个星期仍然存活。该剧的主人公是Henrietta Lacks的女儿Deborah Lacks（Oprah Winfrey），她试图理解一个自己从未见过的母亲，研究她与现代医学发展之间的奇特关联。剧情深刻揭露了医学界的傲慢、自大、种族、贫穷等问题，同时深入剖析「最不可能成为朋友的人」如何建立起「最深厚的友谊」。Rose Byrne扮演年轻女记者Rebecca Skloot，她正在调查Henrietta Lacks的故事及其「不死的」细胞。她与Henrietta的女儿、魅力非凡但性格复杂的Deborah建立了亲密友谊。经过长达十多年的调查研究，Rebecca最终写出了著名的《Henrietta Lacks的不朽人生》（The Immortal Life of Henrietta Lacks）。这本书出版后立刻登上《纽约时报》畅销书排行榜，并且在榜单上停留了整整四年时间，影响力甚为深远。（cr 天涯小筑）', 'http://www.mp4sf.com/upload/vod/20181203-1/afc8d58a5b008a806405f4b6e7bdc3e9.jpg', '美国							', '2018-12-29 07:23:35', '2017						', '剧情							', null, null, null, null, null, null, null, null, '', null, null, null, null, null, null, null, null, null, null);
