INSERT INTO `t_ckplayer_video` VALUES ('1945', 'https://v5.438vip.com/2018/04/18/bsBhbenUamEGrYgo/playlist.m3u8', 'video/m3u8', 'BD1280高清中英双字', '0', '非洲和尚', null, '华侨阿森（陈山河 饰）请来林道士（林正英 饰）参加远在英国的拍卖会，二人将阿森家族祖先的僵尸买下，准备运回国内安葬，岂料飞机半路遭遇浓雾天气，他们失去了方向飞到了非洲，并在跳伞时和僵尸分散免费电影僵尸被当地土著人历苏和族人们带回，而林道士和阿森却陷入了野兽的围困之中。历苏好心帮助了一位抓捕非洲少女的白人女性，她却不思报答，反而脱身而去，计划将土著人用作工具的钻石据为己有。林道士和阿森脱身后再度遇险，又是历苏出手相救，二人入住历苏族人的营地养伤，同时也找到了丢失的僵尸。此时，白人带领众多帮手返回抢夺钻石，林道士派出僵尸大战白人请来的黑巫师……本片旁白由周星驰和吴孟达担任，主角历苏凭借《上帝也疯狂》一举成名。', 'http://www.mp4sf.com/upload/vod/20181203-3/8cc245087cb328bad81d8d096d61e6e8.jpg', '香港							', '2018-12-29 07:28:23', '1991						', '喜剧							', null, null, null, null, null, null, null, null, '', null, null, null, null, null, null, null, null, null, null);
