INSERT INTO `t_ckplayer_video` VALUES ('2133', 'https://v5.438vip.com/2018/04/28/Wxop7kkeiMLjD9Rv/playlist.m3u8', 'video/m3u8', 'BD1280高清中英双字', '0', '福星高照', null, '警界败类三哥（林正英 饰）席卷价值近一亿元的珠宝逃往日本，虽有鸡骨草（成龙 饰）和独角金（元彪 饰）两位干探跟踪追捕，但三哥仍为日本黑社会头目松本所救，鸡骨草二人也陷入危机电影观看迫于公众形象限制，香港警方不愿动用本土警力。曹警司（曹达华 饰）找到正在服刑的罪犯鹧鸪菜（洪金宝 饰），希望借助他的力量将三哥引渡回港。鹧鸪菜随后集结孤儿院的好友花旗参（秦祥林 饰）、犀牛皮（冯淬帆 饰）、大生地（吴耀汉 饰）和罗汉果（曾志伟 饰）。在曹警司的威逼利诱下，五个笑料不断的和女警探霸王花踏上前往日本的旅程……', 'http://www.mp4sf.com/upload/vod/20181203-3/9013ae4047e356510bcc48ae4e70c071.jpg', '香港							', '2018-12-29 07:30:31', '1985						', '喜剧,动作							', null, null, null, null, null, null, null, null, '', null, null, null, null, null, null, null, null, null, null);
